import React, { useState } from "react";
import QRCode from "react-qr-code";
import { ReclaimProofRequest } from "@reclaimprotocol/js-sdk";

function ReclaimDemo() {
  const [requestUrl, setRequestUrl] = useState("");
  const [verificationIdentifier, setVerificationIdentifier] = useState(null);
  const [error, setError] = useState(null);
  const [isVerifying, setIsVerifying] = useState(false);
  const [isVerified, setIsVerified] = useState(false); // New state for success

  const getVerificationReq = async () => {
    try {
      setIsVerifying(true);
      setError(null);

      const APP_ID = "your_actual_app_id_here";
      const APP_SECRET = "your_actual_secret_here";
      const PROVIDER_ID = "your_actual_provider_id_here";

      const reclaimProofRequest = await ReclaimProofRequest.init(
        APP_ID,
        APP_SECRET,
        PROVIDER_ID
      );

      const url = await reclaimProofRequest.getRequestUrl();
      console.log("Request URL:", url);
      setRequestUrl(url);

      await reclaimProofRequest.startSession({
        onSuccess: (proofs) => {
          console.log("Verification success:", proofs);
          setVerificationIdentifier(proofs[0]?.verificationIdentifier);
          setIsVerifying(false);
        },
        onError: (error) => {
          console.error("Verification failed:", error);
          setError("Verification failed. Please try again.");
          setIsVerifying(false);
        },
      });
    } catch (error) {
      console.error("Error creating verification request:", error);
      setError(`An error occurred: ${error.message}`);
      setIsVerifying(false);
    }
  };

  const verifyWithServer = async () => {
    if (!verificationIdentifier) {
      setError("No verification identifier available");
      return;
    }

    try {
      const response = await fetch("http://localhost:3001/verify-proof", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ verificationIdentifier }),
      });

      const result = await response.json();
      if (result.success) {
        setIsVerified(true); // Set success state
      } else {
        setError("Server verification failed");
      }
    } catch (error) {
      console.error("Server error:", error);
      setError("Failed to verify with the server");
    }
  };

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h1>Zero-Knowledge Proof Demo</h1>

      {!isVerified ? (
        <>
          <button onClick={getVerificationReq} disabled={isVerifying}>
            {isVerifying ? "Verifying..." : "Get Verification Request"}
          </button>

          {requestUrl && (
            <div style={{ margin: "20px 0" }}>
              <h3>Scan to Verify:</h3>
              <QRCode value={requestUrl} />
              <p>
                Or click <a href={requestUrl}>here</a> to verify.
              </p>
            </div>
          )}

          {verificationIdentifier && (
            <div>
              <h2>âœ… Verification Successful!</h2>
              <p>Verification ID: {verificationIdentifier}</p>
              <button onClick={verifyWithServer}>Verify with Server</button>
            </div>
          )}
        </>
      ) : (
        <h2>ðŸŽ‰ Access Granted! You are now verified. âœ…</h2>
      )}

      {error && <p style={{ color: "red" }}>{error}</p>}
    </div>
  );
}

export default ReclaimDemo;
